        i0.ɵɵtext(134, " And yet she fought her passion and she died.");
        i0.ɵɵelement(135, "br");
        i0.ɵɵtext(136, " But what she wrote was false.");
        i0.ɵɵelement(137, "br");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(138, "p", 17);
        i0.ɵɵtext(139, " As Theseus listened, overwhelmed by this sum of terrible events, Hippolytus still breathing was carried in. ");
